INSERT INTO `wp_wc_admin_notes` VALUES (51,'avalara_q3-2023_noAvaTax','marketing','en_US','Automatically calculate VAT in real time','Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.','{}','pending','woocommerce.com','2024-11-05 22:22:23',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (52,'woocommerce-usermeta-Sept2023-productvendors','update','en_US','Your store requires a security update','<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.','{}','pending','woocommerce.com','2024-11-05 22:22:23',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (53,'woocommerce-STRIPE-Oct-2023-update-needed','update','en_US','Action required: Security update for WooCommerce Stripe Gateway','<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.','{}','pending','woocommerce.com','2024-11-05 22:22:23',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (54,'amazon-mcf-reviews-2023-12-07','marketing','en_US','Enjoying Amazon MCF for WooCommerce?','We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!','{}','pending','woocommerce.com','2024-11-05 22:22:23',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (55,'remove_estimated_deposits_2024','marketing','en_US','Estimated deposits are going away','To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.','{}','pending','woocommerce.com','2024-11-05 22:22:23',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (56,'wc-admin-install-jp-and-wcs-plugins','info','en_US','Uh oh... There was a problem during the Jetpack and WooCommerce Shipping & Tax install. Please try again.','We noticed that there was a problem during the Jetpack and WooCommerce Shipping &amp; Tax install. Please try again and enjoy all the advantages of having the plugins connected to your store! Sorry for the inconvenience. The \"Jetpack\" and \"WooCommerce Shipping &amp; Tax\" plugins will be installed &amp; activated for free.','{}','unactioned','woocommerce-admin','2024-11-05 22:23:41',NULL,0,'plain','',0,1,'info');
